
public class ForloopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int sum=0;
		for (int i=1 ; i<=5 ; i++) {
			//System.out.println("hellow");
			   sum=sum+i;
			   System.out.println("sum:"+ sum);
			   
		}
		
		System.out.println("sum:"+ sum);
		

	}

}
